mapper.update:
update professor
set
subject_no=#{subject_no}
where professor_id=#{professor_id}

mapper.select:
SELECT * FROM professor where professor_id not in (select member_id from member where member_id in(select member_id from schedule
WHERE to_date(substr(schedule_start_date,1,10),'yyyy-mm-dd') BETWEEN TO_DATE(#{class_start_date}, 'yyyy-mm-dd') AND TO_DATE(#{class_end_date}, 'yyyy-mm-dd')
AND to_date(substr(schedule_start_date,12,length(schedule_start_date)), 'HH24:MI:SS') BETWEEN to_date('09:00:00', 'HH24:MI:SS') AND to_date('12:50:00', 'HH24:MI:SS'))and member_role ='PROFESSOR') and subject_no = #{subject_no}

SELECT * FROM professor where professor_id not in (select member_id from member where member_id in(select member_id from schedule
WHERE to_date(substr(schedule_start_date,1,10),'yyyy-mm-dd') BETWEEN TO_DATE(#{class_start_date}, 'yyyy-mm-dd') AND TO_DATE(#{class_start_date}, 'yyyy-mm-dd')
AND to_date(substr(schedule_start_date,12,length(schedule_start_date)), 'HH24:MI:SS') BETWEEN to_date('13:30:00', 'HH24:MI:SS') AND to_date('17:20:00', 'HH24:MI:SS'))and member_role ='PROFESSOR') and subject_no = #{subject_no}
